def load_fgvc_aircraft_data(data_dir, variant='variant'):
    images_dir = os.path.join(data_dir, 'fgvc-aircraft-2013b', 'data', 'images')
    train_labels_file = os.path.join(data_dir, 'fgvc-aircraft-2013b', 'data', f'images_{variant}_trainval.txt')
    test_labels_file = os.path.join(data_dir, 'fgvc-aircraft-2013b', 'data', f'images_{variant}_test.txt')
    
    def read_labels_file(labels_file):
        with open(labels_file, 'r') as f:
            lines = f.readlines()
        image_paths = []
        labels = []
        for line in lines:
            parts = line.strip().split(' ')
            label = ' '.join(parts[1:])
            image_path = os.path.join(images_dir, parts[0] + '.jpg')
            image_paths.append(image_path)
            labels.append(label)
        return image_paths, labels
    
    train_image_paths, train_labels = read_labels_file(train_labels_file)
    test_image_paths, test_labels = read_labels_file(test_labels_file)
    
    # Encode labels as integers
    unique_labels = sorted(set(train_labels + test_labels))
    label_to_index = {label: index for index, label in enumerate(unique_labels)}
    train_labels = [label_to_index[label] for label in train_labels]
    test_labels = [label_to_index[label] for label in test_labels]
    
    return (train_image_paths, train_labels), (test_image_paths, test_labels), label_to_index


